export const getAllQuestions = async () => {
  return fetch("http://localhost:3001/questions")
    .then((res) => res.json())
    .catch((err) => console.log(err));
};

export const addQuestion = async (question) => {
  return fetch("http://localhost:3001/questions", {
    method: "POST",
    body: JSON.stringify(question),
    headers: {
      "Content-Type": "application/json",
    },
    credentials: "same-origin",
  })
    .then((res) => res.json())
    .catch((err) => console.log(err));
};


export const deleteQuestion = (questionId) => {
  return async (dispatch) => {
    try {
      // Make an HTTP DELETE request to your json-server endpoint
      await axios.delete(`http://localhost:3001/questions/${questionId}`);

      // If the deletion was successful, dispatch the action to update the Redux state
      dispatch({
        type: "DELETE_QUESTION",
        payload: { questionId },
      });
    } catch (error) {
      // Handle errors here, such as displaying an error message
      console.error("Error deleting question:", error);
    }
  };
};
