import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Axios from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await Axios.get(`http://localhost:3001/users?username=${username}&password=${password}`);
      const account = response.data[0];

      if (account) {
        // Kiểm tra vai trò và thực hiện đăng nhập tương ứng
        if (account.role === 'admin') {
          alert('Login with ADMIN');
          // Redirect hoặc thực hiện các hành động liên quan đến admin
        } else {
          alert('Login with User');
          // Redirect hoặc thực hiện các hành động liên quan đến người dùng
        }
      } else {
        alert('Tên đăng nhập hoặc mật khẩu không đúng.');
      }
    } catch (error) {
      console.error('Đã xảy ra lỗi:', error);
    }
  }

  return (
    <Form className='text-center padding1'> 
      <Form.Group controlId="formUsername">
        <Form.Label>Tên đăng nhập</Form.Label>
        <Form.Control
          type="text"
          placeholder="Nhập tên đăng nhập"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </Form.Group>

      <Form.Group controlId="formPassword">
        <Form.Label>Mật khẩu</Form.Label>
        <Form.Control
          type="password"
          placeholder="Nhập mật khẩu"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </Form.Group>

      <Button 
      variant="primary" type="button" onClick={handleLogin}>
        Đăng nhập
      </Button>
    </Form>
  );
}

export default LoginForm;
