import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllQuestions, deleteQuestion } from "../redux/actions/questionsAction";
import { Button, Card, Form, Spinner, Modal } from "react-bootstrap";
import CardQuestion from "../components/CardQuestion";
import { Link } from "react-router-dom";

function ListQuiz() {
  const { questions, isLoading } = useSelector((state) => state.questions);
  const [score, setScore] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [deleteQuestionId, setDeleteQuestionId] = useState("");

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllQuestions());
  }, [dispatch]);

  const handleDelete = () => {
    // Check if deleteQuestionId is not empty before dispatching the delete action
    if (deleteQuestionId) {
      dispatch(deleteQuestion(deleteQuestionId));
      setShowModal(false); // Close the modal after deletion
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setDeleteQuestionId(""); // Reset the deleteQuestionId when closing the modal
  };

  return (
    <div
      style={{
        padding: 40,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 20,
      }}
    >
      {isLoading ? (
        <div style={{ display: "flex", gap: 20 }}>
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
        </div>
      ) : (
        questions.map((question) => (
          <div key={question.id}>
            <h4>
              Question {question.id}: {question.text}
              <Button variant="danger" onClick={() => {
                setDeleteQuestionId(question.id);
                setShowModal(true);
              }}>
                Delete
              </Button>
            </h4>
          </div>
        ))
      )}
      <Link to="/contact">
        <Button type="submit" variant="primary" disabled={isLoading}>
          {isLoading ? "Loading…" : "Add new question"}
        </Button>
      </Link>

      {/* Modal for deleting a question */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Question</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group controlId="deleteQuestionId">
            <Form.Label>Enter Question ID to delete:</Form.Label>
            <Form.Control
              type="text"
              placeholder="Question ID"
              value={deleteQuestionId}
              onChange={(e) => setDeleteQuestionId(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ListQuiz;
