import { addQuestion, getAllQuestions } from "../../apis/index.js";
import { questionsTypes } from "../ActionTypes/questionsTypes.js";

export const fetchAllQuestions = () => async (dispatch) => {
  try {
    dispatch({
      type: questionsTypes.LOAD_QUESTION_LOADING,
      payload: true,
    });
    const res = await getAllQuestions();
    dispatch({
      type: questionsTypes.FETCH_ALL_QUESTIONS,
      payload: res,
    });
  } catch (err) {
    console.log(err);
  } finally {
    dispatch({
      type: questionsTypes.LOAD_QUESTION_LOADING,
      payload: false,
    });
  }
};

export const addNewQuestion = (data) => async (dispatch) => {
  try {
    dispatch({
      type: questionsTypes.LOAD_QUESTION_LOADING,
      payload: true,
    });
    const res = await addQuestion(data);
    dispatch({
      type: questionsTypes.ADD_NEW_QUESTION,
      payload: res,
    });
  } catch (err) {
    console.log(err);
  } finally {
    dispatch({
      type: questionsTypes.LOAD_QUESTION_LOADING,
      payload: false,
    });
  }
};
// questionsAction.js
export const deleteQuestion = (questionId) => ({
  type: "DELETE_QUESTION",
  payload: { questionId },
});

// questionsReducer.js
const questionsReducer = (state = initialState, action) => {
  switch (action.type) {
    case "DELETE_QUESTION":
      return {
        ...state,
        questions: state.questions.filter((question) => question.id !== action.payload.questionId),
      };
    // các trường hợp khác...
    default:
      return state;
  }
};
