import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllQuestions } from "../redux/actions/questionsAction";
import { Card, Form, Spinner } from "react-bootstrap";
import CardQuestion from "../components/CardQuestion";

function Quiz() {
  const { questions, isLoading } = useSelector((state) => state.questions);
  const [score, setScore] = useState(0);
  const [showAlert, setShowAlert] = useState(false);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllQuestions());
  }, []);

  const showCompletionAlert = () => {
    if (score === questions.length) {
      setShowAlert(true);
    } else if (score === 1) {
      setShowAlert(true);
    } else if (score === 2) {
      setShowAlert(true);
    } else if (score === 3) {
      setShowAlert(true);
    } else if (score === 4) {
      setShowAlert(true);
    }
  };

  const handleCloseAlert = () => {
    setShowAlert(false);
  };

  return (
    <div
      style={{
        padding: 40,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 20,
      }}
    >
      {/* Kiểm tra nếu đang loading, hiển thị Spinner */}
      {isLoading ? (
        <div style={{ display: "flex", gap: 20 }}>
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
        </div>
      ) : (
        // Nếu không loading, map qua mảng câu hỏi và hiển thị CardQuestion
        questions.map((question, index) => (
          <CardQuestion
            setScore={setScore}
            question={question}
            index={index}
            key={question.id}
          ></CardQuestion>
        ))
      )}
      {/* Hiển thị điểm số */}
      <p style={{ fontSize: "20px", fontWeight: 700 }}>
        An Phương đã được {score} điểm
      </p>

      {/* Kiểm tra điều kiện để hiển thị thông báo và gọi hàm khi đã trả lời hết */}
      {score >= questions.length && showCompletionAlert()}

      {/* Hiển thị custom modal */}
      {showAlert && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: "20px",
              borderRadius: "10px",
              textAlign: "center",
            }}
          >
            <p>Congratulation, Đúng hết oài Iu bé nhìuuuuu</p>
            <button onClick={handleCloseAlert}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Quiz;
