import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Axios from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await Axios.get(`http://localhost:3001/users?username=${username}&password=${password}`);
      const account = response.data[0];

      if (account) {
        // Kiểm tra vai trò và thực hiện đăng nhập tương ứng
        if (account.role === 'admin') {
          // Đăng nhập với vai trò admin
          // Redirect hoặc thực hiện các hành động liên quan đến admin
        } else {
          // Đăng nhập với vai trò người dùng bình thường
          // Redirect hoặc thực hiện các hành động liên quan đến người dùng
        }
      } else {
        alert('Tên đăng nhập hoặc mật khẩu không đúng.');
      }
    } catch (error) {
      console.error('Đã xảy ra lỗi:', error);
    }
  }



  return (
    <div>
      <input
        type="text"
        placeholder="Tên đăng nhập"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Mật khẩu"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Đăng nhập</button>
    </div>
  );
}


export default LoginForm;
