import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllQuestions, deleteQuestion } from "../redux/actions/questionsAction";
import { Button, Card, Form, Spinner, Modal } from "react-bootstrap";
import CardQuestion from "../components/CardQuestion";
import { Link } from "react-router-dom";

function ListQuiz() {
  const { questions, isLoading } = useSelector((state) => state.questions);
  const [showModal, setShowModal] = useState(false);
  const [deleteQuestionId, setDeleteQuestionId] = useState("");
  const dispatch = useDispatch();

  const handleDelete = async () => {
    if (deleteQuestionId) {
      await dispatch(deleteQuestion(deleteQuestionId));
      setShowModal(false);
    }
  };

  return (
    <div
      style={{
        padding: 40,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 20,
      }}
    >
      {/* ... (other code) */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Question</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Are you sure you want to delete this question?</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ListQuiz;
