import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchAllQuestions } from "../redux/actions/questionsAction";
import { Card, Form, Spinner } from "react-bootstrap";
import CardQuestion from "../components/CardQuestion";

function Quiz() {
  const { questions, isLoading } = useSelector((state) => state.questions);
  const [score, setScore] = useState(0);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllQuestions());
  }, []);

  const showCompletionAlert = () => {
    if (score === questions.length) {
      alert("Congratulation , Đúng hết oài Iu bé nhìuuuuu");
    } else if (score === 1) {
      alert("Vl Thế mà bảo yêu người ta trả lời có 1 câu đúng");
    } else if (score === 2) {
      alert("Haizzz Chả hiểu anh clg cạ !!!!!");
    } else if (score === 3) {
      alert("Ummm Cũng có yêu nhưng mà chưa đủuu");
    } else if (score === 4) {
      alert("Cũng hiểu anh kha khá em ạ nhưng chưa đủ");
    }
  };

  return (
    <div
      style={{
        padding: 40,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 20,
      }}
    >
      {/* Kiểm tra nếu đang loading, hiển thị Spinner */}
      {isLoading ? (
        <div style={{ display: "flex", gap: 20 }}>
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
          <Spinner animation="grow" size="sm" />
        </div>
      ) : (
        // Nếu không loading, map qua mảng câu hỏi và hiển thị CardQuestion
        questions.map((question, index) => (
          <CardQuestion
            setScore={setScore}
            question={question}
            index={index}
            key={question.id}
          ></CardQuestion>
        ))
      )}
      {/* Hiển thị điểm số */}
      <p style={{ fontSize: "20px", fontWeight: 700 }}>
        An Phương đã được {score} điểm
      </p>

      {/* Kiểm tra điều kiện để hiển thị thông báo và gọi hàm khi đã trả lời hết */}
      {score >= questions.length && showCompletionAlert()}
    </div>
  );
}

export default Quiz;
